// Updated query_parser with /oncall command prefix - SILENT SKIP (no error shown)
const input = $input.first().json;

// CRITICAL: Skip bot's own messages to prevent infinite loops
// Check for Adaptive Card attachments (bot's signature)
const hasAdaptiveCard = input.attachments && input.attachments.length > 0 &&
                        input.attachments.some(att => att.contentType === 'application/vnd.microsoft.card.adaptive');
const hasAttachmentPlaceholder = input.body?.content?.includes('<attachment id=');

if (hasAdaptiveCard || hasAttachmentPlaceholder) {
  // This is the bot's own Adaptive Card reply - skip silently
  return {
    json: {
      shouldProcess: false,
      skipReason: "bot_message",
      query: null,
      source: "teams",
      user: "Bot",
      namespace: "default",
      timestamp: new Date().toISOString(),
      threadId: null,
      isReply: false,
      messageId: input.id || null,
      replyToId: input.replyToId || null
    }
  };
}

function extractTeamsMessage(data) {
  if (data.body && data.body.content) {
    const content = data.body.content;
    const plainText = content.replace(/<[^>]*>/g, '').trim();
    return plainText;
  }
  return null;
}

const isTeamsMessage = input.messageType === "message" && input.from !== undefined;
const hasIncomingQuery = (input.query !== undefined && input.query !== null && input.query.trim() !== "") ||
                         (input.prompt !== undefined && input.prompt !== null && input.prompt.trim() !== "");

let query, source, user, namespace, threadId, isReply;

if (isTeamsMessage) {
  query = extractTeamsMessage(input);
  if (!query || query === "") {
    // No text content - skip silently
    return {
      json: {
        shouldProcess: false,
        skipReason: "no_text_content",
        query: null,
        source: "teams",
        user: input.from?.user?.displayName || "Unknown",
        namespace: "default",
        timestamp: new Date().toISOString(),
        threadId: null,
        isReply: false,
        messageId: input.id || null,
        replyToId: input.replyToId || null
      }
    };
  }

  // Check if this is a reply to an existing message
  isReply = input.replyToId !== undefined && input.replyToId !== null;

  // NEW: Check for /oncall command prefix (case-insensitive)
  // Only check if this is NOT a reply (thread replies don't need prefix)
  if (!isReply) {
    const lowerQuery = query.toLowerCase();
    if (!lowerQuery.startsWith('/oncall')) {
      // Message doesn't start with /oncall and it's not a thread reply - skip silently
      return {
        json: {
          shouldProcess: false,
          skipReason: "missing_oncall_prefix",
          query: query,
          source: "teams",
          user: input.from?.user?.displayName || input.from?.user?.email || "Teams User",
          namespace: "default",
          timestamp: new Date().toISOString(),
          threadId: null,
          isReply: false,
          messageId: input.id || null,
          replyToId: input.replyToId || null
        }
      };
    }

    // Strip the /oncall prefix and any extra whitespace
    query = query.replace(/^\/oncall\s*/i, '').trim();

    // Verify there's still a query after removing the prefix
    if (!query || query === "") {
      return {
        json: {
          shouldProcess: false,
          skipReason: "empty_query_after_prefix",
          query: null,
          source: "teams",
          user: input.from?.user?.displayName || input.from?.user?.email || "Teams User",
          namespace: "default",
          timestamp: new Date().toISOString(),
          threadId: null,
          isReply: false,
          messageId: input.id || null,
          replyToId: input.replyToId || null
        }
      };
    }
  }

  source = "teams";
  user = input.from?.user?.displayName || input.from?.user?.email || "Teams User";
  namespace = "default";

  // Extract thread ID for memory tracking
  // Use replyToId as the conversation thread, or message id if it's a new thread
  threadId = isReply ? input.replyToId : input.id;

} else if (hasIncomingQuery) {
  // Direct API calls bypass the /oncall requirement
  query = input.query || input.prompt;
  source = input.context?.source || "on-demand";
  user = input.context?.user || "Unknown";
  namespace = input.namespace || "default";
  isReply = false;
  threadId = Date.now().toString();
} else {
  // Scheduled/cron jobs bypass the /oncall requirement
  query = "Can you check the artemis-auth service in the artemis-auth-preprod namespace";
  source = "scheduled";
  user = "System (Cron)";
  namespace = "default";
  isReply = false;
  threadId = Date.now().toString();
}

// Message should be processed
return {
  json: {
    shouldProcess: true,
    skipReason: null,
    query: query,
    source: source,
    user: user,
    namespace: namespace,
    timestamp: new Date().toISOString(),
    threadId: threadId,
    isReply: isReply,
    messageId: input.id || null,
    replyToId: input.replyToId || null
  }
};

// Get data from previous nodes
const aiOutput = $('AI Agent').first().json.output || "No output received";
const queryData = $('query_parser').first().json;

// Build the Adaptive Card content (as an object first)
const adaptiveCardContent = {
  "type": "AdaptiveCard",
  "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
  "version": "1.4",
  "body": [
    {
      "type": "TextBlock",
      "text": "ü§ñ On-Call Assistant",
      "weight": "Bolder",
      "size": "Large",
      "color": "Accent"
    },
    {
      "type": "TextBlock",
      "text": aiOutput,
      "wrap": true
    },
    {
      "type": "FactSet",
      "facts": [
        {
          "title": "Query:",
          "value": queryData.query
        },
        {
          "title": "User:",
          "value": queryData.user
        },
        {
          "title": "Timestamp:",
          "value": queryData.timestamp
        }
      ]
    }
  ]
};

// Build the final payload - NO "body" wrapper here!
const payload = {
  "body": {
    "contentType": "html",
    "content": "<attachment id=\"1\"></attachment>"
  },
  "attachments": [
    {
      "id": "1",
      "contentType": "application/vnd.microsoft.card.adaptive",
      "content": JSON.stringify(adaptiveCardContent)  // ‚Üê STRINGIFIED!
    }
  ]
};

return {
  json: payload
};

// FINAL FIX: format_conversation_history - Correctly detects bot messages by Adaptive Card presence
const queryData = $('query_parser').first().json;

// Get thread history from Graph API (if this is a reply)
let messages = [];
try {
  const historyResponse = $('get_thread_history').first().json;
  if (historyResponse && historyResponse.value) {
    messages = historyResponse.value;
  }
} catch (error) {
  // No thread history (new thread), return empty array
  return {
    json: {
      conversationHistory: [],
      currentQuery: queryData.query,
      threadId: queryData.threadId,
      messageCount: 0
    }
  };
}

// Helper: Extract text from user's HTML message
function extractTeamsMessage(data) {
  if (data.body && data.body.content) {
    const content = data.body.content;
    const plainText = content.replace(/<[^>]*>/g, '').trim();
    return plainText;
  }
  return null;
}

// Helper: Extract text from bot's Adaptive Card
function extractAdaptiveCardText(data) {
  // Check if this is an Adaptive Card attachment
  if (data.attachments && data.attachments.length > 0) {
    const attachment = data.attachments[0];

    if (attachment.contentType === 'application/vnd.microsoft.card.adaptive') {
      try {
        // Parse the Adaptive Card content
        const cardContent = typeof attachment.content === 'string'
          ? JSON.parse(attachment.content)
          : attachment.content;

        // Extract text from TextBlock elements in the card body
        if (cardContent.body && Array.isArray(cardContent.body)) {
          const textBlocks = cardContent.body
            .filter(block => block.type === 'TextBlock')
            .map(block => block.text)
            .join('\n');

          return textBlocks || '[Adaptive Card - no text extracted]';
        }
      } catch (e) {
        return '[Adaptive Card - parsing failed]';
      }
    }
  }

  return '[Bot message - no extractable text]';
}

// Helper: Check if message is a bot message
function isBotMessage(msg) {
  // Bot messages have Adaptive Card attachments
  if (msg.attachments && msg.attachments.length > 0) {
    const hasAdaptiveCard = msg.attachments.some(
      att => att.contentType === 'application/vnd.microsoft.card.adaptive'
    );
    if (hasAdaptiveCard) return true;
  }

  // Also check for attachment placeholder in body
  if (msg.body && msg.body.content &&
      msg.body.content.includes('<attachment id=')) {
    return true;
  }

  return false;
}

// Build conversation history array
const conversation = [];

for (const msg of messages) {
  // Skip the current message (it will be the "currentQuery")
  if (msg.id === queryData.messageId) {
    continue;
  }

  if (isBotMessage(msg)) {
    // Bot message (assistant response)
    const botText = extractAdaptiveCardText(msg);
    if (botText && botText !== "" && !botText.startsWith('[Bot message -')) {
      conversation.push({
        role: "assistant",
        content: botText
      });
    }
  } else {
    // User message
    const userText = extractTeamsMessage(msg);
    if (userText && userText !== "") {
      conversation.push({
        role: "user",
        content: userText
      });
    }
  }
}

return {
  json: {
    conversationHistory: conversation,
    currentQuery: queryData.query,
    threadId: queryData.threadId,
    messageCount: conversation.length
  }
};

// Build the reply URL from Teams trigger data
  const triggerData = $('Microsoft Teams Trigger').first().json;
  const queryData = $('query_parser').first().json;
  const payload = $input.first().json; // The Adaptive Card payload

  // Extract team and channel IDs from @odata.id
  const odataId = triggerData["@odata.id"];

  // Parse team ID
  const teamIdMatch = odataId.match(/teams\('([^']+)'\)/);
  const teamId = teamIdMatch ? teamIdMatch[1] : null;

  // Parse channel ID
  const channelIdMatch = odataId.match(/channels\('([^']+)'\)/);
  const channelId = channelIdMatch ? channelIdMatch[1] : null;

  // Use threadId from query_parser (handles both new threads and replies correctly)
  const threadId = queryData.threadId;

  if (!teamId || !channelId || !threadId) {
    throw new Error(`Missing required IDs - Team: ${teamId}, Channel: ${channelId}, Thread: ${threadId}`);
  }

  // Build the complete URL
  const replyUrl = `https://graph.microsoft.com/v1.0/teams/${teamId}/channels/${channelId}/messages/${threadId}/replies`;

  return {
    json: {
      url: replyUrl,
      payload: payload,
      teamId: teamId,
      channelId: channelId,
      threadId: threadId
    }
  };
